var React = require('react');
var ReactDOM = require('react-dom');

var Toggle = React.createClass({displayName: "Toggle",
  getInitialState: function () {
    return {
      checked: false
    };
  },

  render: function () {
    return (
      React.createElement("div", {onClick: this.onToggleClicked.bind(this)}, 
        React.createElement("input", {checked: this.state.checked, id: this.props.id, type: "checkbox"}), 
        React.createElement("label", {for: this.props.id}, this.props.label)
      )
    );
  },

  onToggleClicked: function () {
    this.setState({ checked: !this.state.checked });
  }
});

var App = React.createClass({displayName: "App",
  render: function () {
    return (
      React.createElement("div", null, 
        React.createElement("div", {className: "video-container"}, 
          React.createElement("iframe", {width: "100%", height: "100%", src: "https://www.youtube.com/embed/A_vaY_L3PwE", frameborder: "0", gesture: "media", allow: "encrypted-media", allowFullScreen: true})
        ), 
        React.createElement("div", {className: "btn-container"}, 
          React.createElement("button", {className: "btn btn-1 btn-1a"}, "ASMR Me!")
        ), 
        React.createElement("div", {className: "container"}, 
          React.createElement(Toggle, {id: "toggle1", label: "Tapping"}), 
          React.createElement(Toggle, {id: "toggle2", label: "No Talking"}), 
          React.createElement(Toggle, {id: "toggle3", label: "Roleplay"})
        )
      )
    );
  }
});

ReactDOM.render(React.createElement(App, null), document.getElementById('main'));