var React = require('react');
var ReactBootstrap = require('react-bootstrap');
var Navigation = require('react-router').Navigation;

var Nav = ReactBootstrap.Nav;
var Navbar = ReactBootstrap.Navbar;
var NavItem = ReactBootstrap.NavItem;

window.MainNavBar = React.createClass({displayName: "MainNavBar",
  mixins: [Navigation],

  render: function () {
    return (
      React.createElement(Navbar, {inverse: true}, 
        React.createElement(Nav, null, 
          React.createElement(NavItem, {href: "#", onClick: function (e) {this._clickTransition(e, 'home')}.bind(this)}, "Home"), 
          React.createElement(NavItem, {href: "#", onClick: function (e) {this._clickTransition(e, 'projects')}.bind(this)}, "Projects"), 
          React.createElement(NavItem, {href: "#", onClick: function (e) {this._clickTransition(e, 'contact')}.bind(this)}, "Contact"), 
          React.createElement(NavItem, {href: "Resume.pdf"}, "Resume")
        )
      )
    );
  },

  _clickTransition: function (e, name) {
    this.transitionTo(name);
    e.preventDefault();
  }
});
