var React = require('react');

var toggle = React.createClass({displayName: "toggle",
	constructor: function (props) {
		super(props)
		this.state = { checked: false };
	},

	render: function () {
		return (
		  React.createElement("div", null, 
		      React.createElement("input", {checked: this.state.checked, id: this.props.id, type: "checkbox", onClick: this.onInputClicked.bind(this)}), 
	          React.createElement("label", {for: this.props.id}, this.props.label)
	      )
		);
	},

	onInputClicked: function () {
		this.setState({ checked: !this.state.checked });
	}
});
